class CSVDataConverter:
    """Converts raw store data into the monitoring system format"""
    
    def __init__(self):
        self.logger = logger
        self.converted_stores = []
        self.validation_errors = []
        self.regional_stats = defaultdict(int)
    
    def load_and_convert_data(self, output_file: str = "stores_converted.csv") -> bool:
        """Load raw data and convert to proper format"""
        try:
            # Parse raw CSV data
            from io import StringIO
            df = pd.read_csv(StringIO(RAW_STORE_DATA))
            
            self.logger.info(f"Loaded {len(df)} stores from raw data")
            
            # Convert each store
            for idx, row in df.iterrows():
                converted_store = self._convert_store_row(row, idx)
                if converted_store:
                    self.converted_stores.append(converted_store)
                    
            # Generate statistics
            self._generate_statistics()
            
            # Save converted data
            self._save_converted_data(output_file)
            
            # Generate validation report
            self._generate_validation_report()
            
            return True
            
        except Exception as e:
            self.logger.error(f"Failed to convert data: {e}")
            return False
    
    def _convert_store_row(self, row: pd.Series, row_idx: int) -> Dict[str, Any]:
        """Convert a single store row to the monitoring format"""
        try:
            store_number = str(row['store_number']).strip()
            mgmt_interface = str(row['mgmtintname']).strip()
            ip_with_mask = str(row['ip_address']).strip()
            brand = str(row['brand']).strip()
            
            # Validate required fields
            if pd.isna(row['ip_address']) or ip_with_mask == 'nan':
                self.validation_errors.append(f"Row {row_idx}: Missing IP address for store {store_number}")
                return None
            
            # Parse IP and mask
            if '/' not in ip_with_mask:
                self.validation_errors.append(f"Row {row_idx}: Invalid IP format for store {store_number}: {ip_with_mask}")
                return None
                
            gateway_ip, subnet_mask = ip_with_mask.split('/')
            
            # Validate IP address
            try:
                ipaddress.IPv4Address(gateway_ip)
            except ipaddress.AddressValueError:
                self.validation_errors.append(f"Row {row_idx}: Invalid IP address for store {store_number}: {gateway_ip}")
                return None
            
            # Calculate network information
            network_cidr = self._calculate_network_cidr(gateway_ip, subnet_mask)
            device_ip = self._calculate_device_ip(gateway_ip, subnet_mask)
            region = self._determine_region(gateway_ip)
            
            # Track regional statistics
            self.regional_stats[region] += 1
            
            # Create converted store record
            converted_store = {
                'store_number': store_number,
                'store_name': f"Store_{store_number}",
                'mgmt_interface': mgmt_interface,
                'gateway_ip': gateway_ip,
                'device_ip': device_ip,
                'subnet_mask': subnet_mask,
                'network_cidr': network_cidr,
                'region': region,
                'brand': brand,
                'enabled': True,
                'priority': 1,
                'tags': f"{brand.lower()},{mgmt_interface},{region.lower()}",
                'validation_status': 'Valid'
            }
            
            return converted_store
            
        except Exception as e:
            self.validation_errors.append(f"Row {row_idx}: Error processing store {store_number}: {str(e)}")
            return None
    
    def _calculate_network_cidr(self, gateway_ip: str, subnet_mask: str) -> str:
        """Calculate CIDR notation from IP and subnet mask"""
        try:
            # Convert subnet mask to prefix length
            if subnet_mask.count('.') == 3:  # Full dotted decimal mask
                network = ipaddress.IPv4Network(f"{gateway_ip}/{subnet_mask}", strict=False)
                return str(network.network) + '/' + str(network.prefixlen)
            else:  # Already CIDR notation
                network = ipaddress.IPv4Network(f"{gateway_ip}/{subnet_mask}", strict=False)
                return str(network.network) + '/' + str(network.prefixlen)
        except Exception as e:
            self.logger.warning(f"Error calculating CIDR for {gateway_ip}/{subnet_mask}: {e}")
            return f"{gateway_ip}/24"  # Default fallback
    
    def _calculate_device_ip(self, gateway_ip: str, subnet_mask: str) -> str:
        """Calculate device IP for monitoring (typically .10 in the subnet)"""
        try:
            gateway = ipaddress.IPv4Address(gateway_ip)
            network = ipaddress.IPv4Network(f"{gateway_ip}/{subnet_mask}", strict=False)
            
            # Try gateway + 9 (so .1 becomes .10)
            device_ip = str(gateway + 9)
            if ipaddress.IPv4Address(device_ip) in network:
                return device_ip
            
            # Try gateway + 10 as fallback
            device_ip = str(gateway + 10)
            if ipaddress.IPv4Address(device_ip) in network:
                return device_ip
            
            # Use first available host IP
            hosts = list(network.hosts())
            if len(hosts) > 1:
                return str(hosts[1])  # Second host (first is usually gateway)
            else:
                return str(hosts[0]) if hosts else gateway_ip
                
        except Exception as e:
            self.logger.warning(f"Error calculating device IP for {gateway_ip}: {e}")
            # Fallback: replace last octet with 10
            parts = gateway_ip.split('.')
            if len(parts) == 4:
                parts[-1] = '10'
                return '.'.join(parts)
            return gateway_ip
    
    def _determine_region(self, gateway_ip: str) -> str:
        """Determine region based on IP address ranges"""
        try:
            ip = ipaddress.IPv4Address(gateway_ip)
            
            # Regional IP mapping based on observed data
            if ip in ipaddress.IPv4Network('10.208.0.0/16'):
                return "South"
            elif ip in ipaddress.IPv4Network('10.210.0.0/16'):
                return "Central"  
            elif ip in ipaddress.IPv4Network('10.211.0.0/16'):
                return "East"
            elif ip in ipaddress.IPv4Network('10.212.0.0/16'):
                return "West"
            elif ip in ipaddress.IPv4Network('10.213.0.0/16'):
                return "North"
            elif ip in ipaddress.IPv4Network('10.214.0.0/16'):
                return "Northwest"
            elif ip in ipaddress.IPv4Network('10.215.0.0/16'):
                return "Northeast"
            elif ip in ipaddress.IPv4Network('192.168.0.0/16'):
                return "Special"  # For the 192.168.1.99 entry
            else:
                return "Unknown"
        except Exception as e:
            self.logger.warning(f"Error determining region for {gateway_ip}: {e}")
            return "Unknown"
    
    def _generate_statistics(self):
        """Generate conversion statistics"""
        total_stores = len(self.converted_stores)
        
        self.logger.info("CONVERSION STATISTICS")
        self.logger.info("=" * 50)
        self.logger.info(f"Total stores processed: {total_stores}")
        self.logger.info(f"Conversion errors: {len(self.validation_errors)}")
        self.logger.info(f"Success rate: {(total_stores / (total_stores + len(self.validation_errors)) * 100):.1f}%")
        
        self.logger.info("\nREGIONAL DISTRIBUTION:")
        for region, count in sorted(self.regional_stats.items()):
            percentage = (count / total_stores * 100) if total_stores > 0 else 0
            self.logger.info(f"  {region}: {count} stores ({percentage:.1f}%)")
    
    def _save_converted_data(self, output_file: str):
        """Save converted data to CSV file"""
        try:
            if not self.converted_stores:
                self.logger.warning("No stores to save")
                return
            
            # Convert to DataFrame
            df = pd.DataFrame(self.converted_stores)
            
            # Save to CSV
            df.to_csv(output_file, index=False)
            self.logger.info(f"Converted data saved to: {output_file}")
            
            # Also save a sample for verification
            sample_file = output_file.replace('.csv', '_sample.csv')
            df.head(10).to_csv(sample_file, index=False)
            self.logger.info(f"Sample data saved to: {sample_file}")
            
        except Exception as e:
            self.logger.error(f"Error saving converted data: {e}")
    
    def _generate_validation_report(self):
        """Generate validation report"""
        report_file = "validation_report.txt"
        
        try:
            with open(report_file, 'w') as f:
                f.write("STORE DATA CONVERSION VALIDATION REPORT\n")
                f.write("=" * 50 + "\n\n")
                
                f.write(f"Total stores converted: {len(self.converted_stores)}\n")
                f.write(f"Total validation errors: {len(self.validation_errors)}\n\n")
                
                if self.validation_errors:
                    f.write("VALIDATION ERRORS:\n")
                    f.write("-" * 20 + "\n")
                    for error in self.validation_errors:
                        f.write(f"  {error}\n")
                    f.write("\n")
                
                f.write("REGIONAL STATISTICS:\n")
                f.write("-" * 20 + "\n")
                for region, count in sorted(self.regional_stats.items()):
                    percentage = (count / len(self.converted_stores) * 100) if self.converted_stores else 0
                    f.write(f"  {region}: {count} stores ({percentage:.1f}%)\n")
                
                # Network analysis
                f.write("\nNETWORK ANALYSIS:\n")
                f.write("-" * 20 + "\n")
                network_counts = defaultdict(int)
                for store in self.converted_stores:
                    network = store['network_cidr'].split('/')[0]
                    major_network = '.'.join(network.split('.')[:2]) + '.0.0/16'
                    network_counts[major_network] += 1
                
                for network, count in sorted(network_counts.items()):
                    f.write(f"  {network}: {count} stores\n")
            
            self.logger.info(f"Validation report saved to: {report_file}")
            
        except Exception as e:
            self.logger.error(f"Error generating validation report: {e}")
    
    def get_converted_data(self) -> List[Dict[str, Any]]:
        """Return converted store data"""
        return self.converted_stores
    
    def get_validation_errors(self) -> List[str]:
        """Return validation errors"""
        return self.validation_errors

